import React, { useState, useEffect, useRef } from "react";
import { io } from "socket.io-client";
import axios from "axios";
import MessageBubble from "./MessageBubble";

const socket = io("http://localhost:5000");

const ChatBox = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [sessionId, setSessionId] = useState(Date.now().toString());
  const [streamingResponse, setStreamingResponse] = useState("");
  const chatContainerRef = useRef(null);
  const messagesEndRef = useRef(null); // Reference to the last message

  useEffect(() => {
    socket.on("bot_response", (data) => {
      setMessages((prevMessages) => {
        const updatedMessages = [...prevMessages];
        
        // If last message is from bot, update it with new text
        if (updatedMessages.length > 0 && updatedMessages[updatedMessages.length - 1].sender === "bot") {
          updatedMessages[updatedMessages.length - 1].text += data.response;
        } else {
          // Otherwise, add a new bot message
          updatedMessages.push({ sender: "bot", text: data.response });
        }
        
        return updatedMessages;
      });
    });
  
    return () => {
      socket.off("bot_response");
    };
  }, []);
  

  useEffect(() => {
    if (streamingResponse) {
      setMessages((prevMessages) => [
        ...prevMessages.slice(0, -1),
        { sender: "bot", text: streamingResponse }
      ]);
    }
    scrollToBottom(); // Scroll when bot streams response
  }, [streamingResponse]);

  useEffect(() => {
    scrollToBottom(); // Scroll when messages update
  }, [messages]);

  const scrollToBottom = () => {
    setTimeout(() => {
      messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
    }, 100); // Delayed scroll for smoother effect
  };

  const sendMessage = () => {
    if (!input.trim()) return;

    const newMessage = { sender: "user", text: input };
    setMessages((prevMessages) => [...prevMessages, newMessage]);

    setStreamingResponse(""); // Clear previous bot response
    setMessages((prevMessages) => [...prevMessages, { sender: "bot", text: "" }]); // Add empty bot message

    socket.emit("user_message", { session_id: sessionId, message: input });

    setInput("");
  };

  const generateSummary = async () => {
    try {
      const response = await axios.post("http://localhost:5000/generate_summary", { session_id: sessionId });
      alert("Chat Summary:\n" + response.data.summary);
    } catch (error) {
      console.error("Error generating summary:", error);
    }
  };

  const downloadChat = async () => {
    try {
      const response = await axios.post("http://localhost:5000/download_chat", { session_id: sessionId }, { responseType: "blob" });

      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement("a");
      link.href = url;
      link.setAttribute("download", `chat_session_${sessionId}.pdf`);
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
    } catch (error) {
      console.error("Error downloading chat:", error);
    }
  };

  return (
    <div className="chat-container">
      <div className="chat-box" ref={chatContainerRef}>
        {messages.map((msg, index) => (
          <MessageBubble key={index} sender={msg.sender} text={msg.text} />
        ))}
        {/* Invisible element to trigger auto-scroll */}
        <div ref={messagesEndRef} />
      </div>
      <div className="input-area">
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyPress={(e) => e.key === "Enter" && sendMessage()}
          placeholder="Ask anything..." 
        />
        <button onClick={sendMessage}>➔</button>
      </div>
      <div className="buttons">
        <button onClick={generateSummary}>Generate Summary</button>
        <button onClick={downloadChat}>Download Chat</button>
      </div>
    </div>
  );
};

export default ChatBox;
