import React, { useState, useEffect, useRef } from "react";
import { io } from "socket.io-client";
import axios from "axios";
import MessageBubble from "./MessageBubble";

const socket = io("http://localhost:5000");

const ChatBox = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [sessionId] = useState(Date.now().toString());
  const messagesEndRef = useRef(null);
  const [streamingResponse, setStreamingResponse] = useState("");

  useEffect(() => {
    socket.on("bot_response", (data) => {
      setStreamingResponse((prev) => prev + data.response);
    });

    return () => {
      socket.off("bot_response");
    };
  }, []);

  useEffect(() => {
    if (streamingResponse) {
      setMessages((prevMessages) => {
        const lastMessage = prevMessages[prevMessages.length - 1];
        if (lastMessage?.sender === "bot") {
          return [
            ...prevMessages.slice(0, -1),
            { sender: "bot", text: streamingResponse }
          ];
        } else {
          return [...prevMessages, { sender: "bot", text: streamingResponse }];
        }
      });
    }
    scrollToBottom();
  }, [streamingResponse]);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const scrollToBottom = () => {
    setTimeout(() => {
      messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
    }, 100);
  };

  const sendMessage = () => {
    if (!input.trim()) return;

    setMessages((prevMessages) => [
      ...prevMessages,
      { sender: "user", text: input },
      { sender: "bot", text: "" }
    ]);

    setStreamingResponse("");

    socket.emit("user_message", { session_id: sessionId, message: input });

    setInput("");
  };

  const generateSummary = async () => {
    try {
      const response = await axios.post("http://localhost:5000/generate_summary", { session_id });
      alert("Chat Summary:\n" + response.data.summary);
    } catch (error) {
      console.error("Error generating summary:", error);
    }
  };

  const downloadChat = async () => {
    try {
      const response = await axios.post("http://localhost:5000/download_chat", { session_id }, { responseType: "blob" });

      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement("a");
      link.href = url;
      link.setAttribute("download", `chat_session_${sessionId}.pdf`);
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
    } catch (error) {
      console.error("Error downloading chat:", error);
    }
  };

  return (
    <div className="chat-container">
      <div className="chat-box">
        {messages.map((msg, index) => (
          <MessageBubble key={index} sender={msg.sender} text={msg.text} />
        ))}
        <div ref={messagesEndRef} />
      </div>
      <div className="input-area">
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyPress={(e) => e.key === "Enter" && sendMessage()}
          placeholder="Ask anything..."
        />
        <button onClick={sendMessage}>âž”</button>
      </div>
      <div className="buttons">
        <button onClick={generateSummary}>Generate Summary</button>
        <button onClick={downloadChat}>Download Chat</button>
      </div>
    </div>
  );
};

export default ChatBox;
