import React from "react";

const MessageBubble = ({ text, sender }) => {
  return (
    <div
      className={`p-3 my-2 max-w-xs sm:max-w-sm md:max-w-md lg:max-w-lg rounded-lg ${
        sender === "user"
          ? "bg-blue-500 text-white self-end" // User messages (align right, blue)
          : "bg-gray-700 text-white self-start" // Bot messages (align left, gray)
      }`}
    >
      {text}
    </div>
  );
};

export default MessageBubble;
