import React from "react";

const MessageBubble = ({ text, sender }) => {
  const isUser = sender === "user";

  // Handle the speech functionality
  const handleSpeech = () => {
    const speech = new SpeechSynthesisUtterance(text);
    speech.rate = 1;  // Adjust the rate of speech (1 is normal speed)
    speech.pitch = 1; // Adjust the pitch (1 is normal pitch)
    speech.volume = 1; // Adjust volume (1 is max volume)
    window.speechSynthesis.speak(speech);
  };

  return (
    <div className="message-bubble">
      <div className={`${sender === "user" ? "user-message" : "bot-message"}`}>
        {/* Render the message content with HTML formatting (line breaks, etc.) */}
        <div
          className="message-text"
          dangerouslySetInnerHTML={{ __html: text }} // Use dangerouslySetInnerHTML to render HTML content
        />
        {/* Add speaker button if it's a bot message */}
        {sender === "bot" && (
          <button onClick={handleSpeech} className="speech-button">
            ðŸ”Š
          </button>
        )}
      </div>
    </div>
  );
};

export default MessageBubble;
